                        StreamWriter writer = new StreamWriter(stream);
                        writer.Write(Resources.Menu);
                        writer.Flush();
                        stream.Position = 0;
                        StreamReader Menu = new StreamReader(stream);
                        vdFramedControl1.LoadMenu (Menu,"C:\\vdraw6\\REDIS\\AnyCPU\\images");
